public static boolean isArmor(Material material) {
	return HELMETS.contains(material) || CHESTPLATES.contains(material) || LEGGINGS.contains(material) || BOOTS.contains(material);
}

public static boolean isTool(Material material) {
	return PICKAXES.contains(material) || AXES.contains(material) || SHOVELS.contains(material) || HOES.contains(material) || material == Material.SHEARS || material == Material.SHIELD ||
			material == Material.FISHING_ROD || material == Material.CARROT_STICK;
}

public static boolean isMeleeWeapon(Material material) {
	return SWORDS.contains(material) || AXES.contains(material);
}

public static boolean isInteractable(Material material) {
	return PRESSURE_PLATES.contains(material) || BUTTONS.contains(material) || DOORS.contains(material) || TRAPDOORS.contains(material) || FENCE_GATES.contains(material) ||
			CHESTS.contains(material) || FURNACES.contains(material) || DAYLIGHT_SENSORS.contains(material) || REDSTONE_COMPARATORS.contains(material) || REDSTONE_REPEATERS.contains(material) ||
			material == Material.ANVIL || material == Material.BEACON || material == Material.BED_BLOCK || material == Material.BREWING_STAND || material == Material.CAKE_BLOCK ||
			material == Material.CAULDRON || material == Material.WORKBENCH || material == Material.ENCHANTMENT_TABLE || material == Material.JUKEBOX || material == Material.NOTE_BLOCK ||
			material == Material.TNT || material == Material.DROPPER || material == Material.DISPENSER || material == Material.TRIPWIRE || material == Material.OBSERVER ||
			material == Material.HOPPER || material == Material.LEVER;
}
